dict.string:
# We default to Preview output, if nothing else selected. # Valid choices are: preview, texniscope. V=${TM_LATEX_VIEWER:=html}
# Set up some variables we need. TEX=${TM_TEX:=pdflatex} OPTIONS=${TM_TEX_OPTIONS:=-interaction=nonstopmode -file-line-error-style} M=${TM_LATEX_MASTER:=$TM_FILEPATH} DIR=`dirname "$M"` FILE=`basename "$M"` BASE=`basename -s.tex "$M"`
# Switch to the right directory. cd "$TM_PROJECT_DIRECTORY" cd "$DIR"
# Prepare output window. cat <<"EOF" <html><head><style> * { color: #998; } strong { color: #000; } a { color: #000; text-decoration: none; } a:hover { color: #000; text-decoration: underline; } </style></head><body><pre> EOF
# Compile. "$TEX" $OPTIONS "$FILE" | "$TM_BUNDLE_PATH/latexErrWarnHtml.py"
# View... case $V in
html) echo "Done!" ;;
preview) echo echo "Previewing..." echo $FILE | perl -e 'while(<>){s/\.tex$/.pdf/;print;}' | xargs open ;;
texniscope) echo echo "Previewing..." echo $FILE | perl -e 'while(<>){s/\.tex$/.pdf/;print;}' | xargs open -a TeXniscope ;;
esac
