dict.string:
# We default to Preview output, if nothing else selected. # Valid choices are: preview, texniscope. V=${TM_LATEX_VIEWER:=preview}
# Set up some variables we need. TEX=${TM_TEX:=pdflatex} OPTIONS=${TM_TEX_OPTIONS:=-interaction=nonstopmode -file-line-error-style} M=${TM_LATEX_MASTER:=$TM_FILEPATH} DIR=`dirname "$M"` FILE=`basename "$M"` BASE=`basename -s.tex "$M"`
# Switch to the right directory. cd "$TM_PROJECT_DIRECTORY" cd "$DIR"
# Compile and view... case $V in
html) echo HTML view is not yet supported. echo Please select either 'preview' or 'texniscope'. ;;
preview) "$TEX" $OPTIONS "$FILE" | perl -e 'while(<>){$f.=$_}$_=$f;s/([^\n])\n([^\n])/$1$2/g;print;' echo echo "Previewing..." echo $FILE | perl -e 'while(<>){s/\.tex$/.pdf/;print;}' | xargs open ;;
texniscope) "$TEX" $OPTIONS "$FILE" | "$TM_BUNDLE_PATH/latexErrWarn.py" echo echo "Previewing..." echo $FILE | perl -e 'while(<>){s/\.tex$/.pdf/;print;}' | xargs open -a TeXniscope ;;
esac
