dict.string:
if [[ -z $TM_DIRECTORY ]]
	then filename=$TM_DROPPED_FILE
	else 
		filename=${TM_DROPPED_FILE#${TM_DIRECTORY}}
fi
echo "\\\\begin{figure}[\${1:htbp}]
	\\\\begin{center}
		\\\\includegraphics[height=\${2:3in}]{$filename}
	\\\\end{center}
	\\\\caption{\${4:caption}}
	\\\\label{fig:\${5:label}}
\\\\end{figure}"
